// Creating a custom structure which includes the data word and TLAST signal.
// ACLK, ARESETN, TREADY, TDATA, TVALID are essential signals for AXIS.
// TLAST is a sideband signal which is optional in AXIS.
// However, it is necessary for us since we connecting M_AXIS to AXI DMA.
// So, we create a struct with data (TDATA) and last (TLAST). The rest of the essential AXIS signals are automatically dealt with by the HLS tool.

struct AXIS_wLAST{
	int data;
	bool last;
};

// Definitions for neural network.
// number of test data
#define NUM_TEST_DATA 36
// number of input neurons
#define NUM_FEATURES 13
// number of neurons in hidden layer
#define NUM_HIDDEN_NODES 5
// number of neurons in output layer
#define NUM_OUTPUT_NODES 3

#define FRACTIONAL_BITS 8
#define ONE (1 << FRACTIONAL_BITS)

// weight matrices
int16_t weights_IH[NUM_FEATURES][NUM_HIDDEN_NODES];
int16_t weights_HO[NUM_HIDDEN_NODES][NUM_OUTPUT_NODES];
// vector to store results in hidden and output layer
int16_t values_hidden_layer[NUM_HIDDEN_NODES];
int16_t values_output_layer[NUM_OUTPUT_NODES];
// array to store test data
int16_t test_data[NUM_FEATURES];

// functions declaration
int mul(int16_t a, int16_t b);
//int16_t fixed_sigmoid(int16_t x);

